                                      //
                                      var blogDir = 'ltr',
                                          numPosts = 25,
                                          numPages = 5;

                                      if(blogDir === 'ltr'){
                                        blogDir = 'fa-angle-double-right';
                                        var blogDirRev = 'fa-angle-double-left';
                                      }else if(blogDir === 'rtl'){
                                        blogDir = 'fa-angle-double-left';
                                        var blogDirRev = 'fa-angle-double-right';
                                      }

                                      function pageNavi(o) {
                                        var entries = o.feed.entry;
                                        if(!entries) {
                                          $('#explore-more').remove();
                                          return;
                                        }

                                        var m = location.href,
                                            l = m.indexOf("/search/label/") != -1,
                                            a = l ? m.substr(m.indexOf("/search/label/") + 14, m.length) : "";
                                        a = a.indexOf("?") != -1 ? a.substr(0, a.indexOf("?")) : a;
                                        var g = l ? "/search/label/" + a + "?updated-max=" : "/search?updated-max=",
                                            k = entries.length,
                                            e = Math.ceil(k / numPosts);

                                        if (e <= 1) {
                                          return
                                        }
                                        var n = 1,
                                            h = [""];
                                        l ? h.push("/search/label/" + a + "?max-results=" + numPosts) : h.push("/?max-results=" + numPosts);
                                        for (var d = 2; d <= e; d++) {
                                          var c = (d - 1) * numPosts - 1,
                                              b = o.feed.entry[c].published.$t,
                                              f = b.substring(0, 19) + b.substring(23, 29);

                                          if (m.indexOf(f) != -1) {
                                            n = d
                                          }
                                          h.push(g + f + "&max-results=" + numPosts)
                                        }
                                        pageNavi.show(h, n, e)
                                      }
                                      pageNavi.show = function(f, e, a) {
                                        var d = Math.floor((numPages - 1) / 2),
                                            g = numPages - 1 - d,
                                            c = e - d;
                                        if (c <= 0) {
                                          c = 1
                                        }
                                        endPage = e + g;
                                        if ((endPage - c) < numPages) {
                                          endPage = c + numPages - 1
                                        }
                                        if (endPage > a) {
                                          endPage = a;
                                          c = a - numPages + 1
                                        }
                                        if (c <= 0) {
                                          c = 1
                                        }
                                        var b = '';

                                        if (e > 1) {
                                          b += '<a id="prevPage" href="' + f[e - 1] + '#filters"><i class="fa '+ blogDirRev +'"></i></a>'
                                        }
                                        for (i = c; i <= endPage; ++i) {
                                          if (i == e) {
                                            b += '<span class="current">' + i + "</span>"
                                          } else {
                                            b += '<a href="' + f[i] + '#filters">' + i + "</a>"
                                          }
                                        }
                                        if (e < a) {
                                          b += '<a id="nextPage" href="' + f[e + 1] + '#filters"><i class="fa '+ blogDir +'"></i></a>'
                                        }
                                        document.write(b);
                                      };
                                      (function() {
                                        var b = location.href;
                                        if (b.indexOf("?q=") != -1 || b.indexOf(".html") != -1) {
                                          return
                                        }
                                        var d = b.indexOf("/search/label/") + 14;
                                        if (d != 13) {
                                          var c = b.indexOf("?"),
                                              a = (c == -1) ? b.substring(d) : b.substring(d, c);
                                          document.write('<script type="text/javascript" src="/feeds/posts/summary/-/' + a + '?alt=json-in-script&callback=pageNavi&max-results=99999"><\/script>')
                                        } else {
                                          document.write('<script type="text/javascript" src="/feeds/posts/summary?alt=json-in-script&callback=pageNavi&max-results=99999"><\/script>')
                                        }
                                      })();
                                      //
                                    
                                  
                                
                              
                              
                                
                                  
                                



                                  


                                  
                                

                                




                                      
                                        
                                        
                                        
                                      
                                    





                                         

                                        
                                          
                                          
                                          
                                        
                                      



                                    

                                  
                                







                                  




                                      
                                        
                                      
                                    
                                  
                                
                              
                              
                              
                              
                                




                                      
                                    
                                  
                                
                              
                              


                                  
                                  


                                    
                                    
                                    
                                    


                                    
                                    
                                    
                                  
                                  
                                  
